let WeavrComponents
let loginPasswordComponent

//
// Utility Methods
//

/**
 * Get Element by ID from the document
 * @param{String} id The id of the elementinit-
 * @returns {HTMLElement}
 */
function getElement(id) {
    return document.getElementById(id)
}

/**
 * Update messages shown when initialising Weavr components
 * @param{Boolean} success
 * @param{String} message
 */
function setInitMessage(success, message) {
    initMessageElement.innerHTML =
        message + `<br/>(${new Date().toLocaleString()})`
    initMessageElement.className = success ? 'text-success' : 'text-error'
}

/**
 * Clear message shown when initialising Weavr components
 */
function clearInitMessage() {
    initMessageElement.innerHTML = ''
}

/**
 * Store initialisation parameters in localstorage
 * @param{string} uiKey
 */
function storeUiKeys(uiKey) {
    if (!USE_STORE) return
    localStorage.setItem(STORAGE_UI_KEY, uiKey)
}

/**
 * Store initialisation parameters in localstorage
 * @param{string} apiKey
 */
function storeApiKeys(apiKey) {
    if (!USE_STORE) return
    localStorage.setItem(STORAGE_API_KEY, apiKey)
}

/**
 * Store initialisation parameters in localstorage
 * @param{string} username
 * @param{string} password
 */
function storeKeys(username, password) {
    if (!USE_STORE) return
    localStorage.setItem(STORAGE_KEY, JSON.stringify({username, password}))
}

/**
 * Load initialisation parameters if present in local storage
 */
function loadKeys() {
    if (!USE_STORE) return
    const uiKeyString = localStorage.getItem(STORAGE_UI_KEY) || ''
    uiKeyElement.value = uiKeyString.trim()

    const apiKeyString = localStorage.getItem(STORAGE_API_KEY) || ''
    apiKeyElement.value = apiKeyString.trim()

    const keysString = localStorage.getItem(STORAGE_KEY)
    if (keysString) {
        try {
            const keys = JSON.parse(keysString)
            usernameElement.value = keys.username.trim()
            passwordElement.value = keys.password.trim()
        } catch {
        }
    }
}

//
// Initialisation methods
//

function submitUiForm(ev) {
    ev.preventDefault()

    const uiKeyElementValue = uiKeyElement.value.trim()
    const apiKeyElementValue = apiKeyElement.value.trim()

    const uiKey = uiKeyElementValue || UIKEY.trim()
    const apiKey = apiKeyElementValue || APIKEY.trim()

    if (!uiKey) {
        setInitMessage(false, 'UI Key is required to initialise components')
        return false
    }
    if (!apiKey) {
        setInitMessage(
            false,
            'API Key is required to perform actions in the multi api'
        )
        return false
    }

    // Show a message to user that the KEYs user are from the env.js
    if (!uiKeyElementValue && !apiKeyElementValue){
        if (uiKey === UIKEY && apiKey === APIKEY) {
            setInitMessage(
                true,
                'You are using env.js declared keys. In order to use custom keys you are required to fill in the respective fields.'
            )
        }
    }



    storeUiKeys(uiKey)
    storeApiKeys(apiKey)
    initWeavrComponents(uiKey)

    initController()

    return true
}

function initController() {
    initLoginPassword()
    initialiseComponentsPreToken()

}

function initLoginPassword() {

    loginPasswordComponent = WeavrComponents.capture.password('password', {
        placeholder: 'Password'
    })

    loginPasswordComponent.mount('#input_password')
}

/**
 * Method starts the initialisation process
 * if the UI Key and Auth Token are present
 * @param{Event} ev
 */
function submitLoginForm(ev) {
    ev.preventDefault()

    const username = usernameElement.value.trim()

    if (!WeavrComponents && !submitUiForm(ev)) return

    loginPasswordComponent.createToken().then((res) => {
        console.log('Login pass tokenisation result:', res)

        const password = res.tokens.password

        if (!username || !password) {
            setInitMessage(false, 'Username and Password must be provided')
            return
        }

        storeKeys(username, password)
        clearInitMessage()

        login(username, password).then((resAuthToken) => {
            if (!resAuthToken) return
            authTokenElement.value = resAuthToken
            setUserToken(resAuthToken)
        })
    })
}

function login(username, tokenisedPassword) {
    const apiKey = apiKeyElement.value.trim()
    return fetch('https://qa.weavr.io/multi/login_with_password', {
        method: 'POST',
        headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
            'api-key': apiKey
        },
        body: JSON.stringify({
            email: username,
            password: {value: tokenisedPassword}
        })
    })
        .catch((e) => {
            setInitMessage(false, 'Failed to login<br/>' + e.toString())
        })
        .then((res) => {
            return res.json()
        })
        .then((res) => {
            const token = res?.token
            if (!token) {
                setInitMessage(false, 'Auth Token not returned')
            } else {
                return token
            }
        })
}

function issueChallenge() {
    const authToken = authTokenElement.value
    if (!authToken) {
        setInitMessage(false, 'Requires auth token to issue challenge')
        return
    }

    const apiKey = apiKeyElement.value.trim()
    fetch('http://https://qa.weavr.io/multi/stepup/challenges/otp/sms', {
        method: 'POST',
        headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
            'api-key': apiKey,
            Authorization: 'Bearer ' + authTokenElement.value
        }
    })
        .catch((e) => {
            setInitMessage(
                false,
                'Failed to issue challenge<br/>' + e.toString()
            )
        })
        .then(() => {
            setInitMessage(true, 'Challenge Issued')
        })
}

function verifyChallenge(ev) {
    ev.preventDefault()

    const authToken = authTokenElement.value
    if (!authToken) {
        setInitMessage(false, 'Requires auth token to verify challenge')
        return
    }

    const verificationCode = challengeElement.value || '123456'
    const apiKey = apiKeyElement.value.trim()

    return fetch('https://qa.weavr.io/multi/stepup/challenges/otp/sms/verify', {
        method: 'POST',
        headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
            'api-key': apiKey,
            Authorization: 'Bearer ' + authTokenElement.value
        },
        body: JSON.stringify({
            verificationCode
        })
    })
        .catch((e) => {
            setInitMessage(
                false,
                'Failed to issue challenge<br/>' + e.toString()
            )
        })
        .then(() => {
            setInitMessage(true, 'Challenge Verified')
        })
}

/**
 * Initialise Weavr UX components using UI Key
 * @param{String} uiKey
 */
function initWeavrComponents(uiKey) {
    const fontSrc =
        'data:font/opentype;base64,'
    WeavrComponents = window.weavr.init(uiKey, {
        fonts: [
            {
                family: 'Fuggles',
                src: `url('https://fonts.googleapis.com/css2?family=Fuggles')`
            },
            {
                family: '"Zen Loop"',
                src: `url('${fontSrc}')`
            },
            {
                cssSrc: FONTS_CSS_SRC
            }
        ]
    })

    if (initStateBtn) {
        initStateBtn.value = 'Initalised'
        initStateBtn.disabled = true
    }

}


/**
 * Set User's Authorisation Token
 * @param{String} token User's Authorisation Token
 */
function setUserToken(token) {
    if (!WeavrComponents) {
        // components are not initialised
        setInitMessage(
            false,
            'Weavr component is not initialised, set UI Key to initialise'
        )
        return
    }

    WeavrComponents.setUserToken('Bearer ' + token)
        .then(() => {
            // Auth token set successfully
            setInitMessage(true, 'Weavr component initialised')

            initialiseComponentsPostToken()
        })
        .catch((e) => {
            // Failed to set auth token
            setInitMessage(false, `Failed to set Auth Token<br />${e.message}`)
        })
}

function initialiseComponentsPreToken() {

    const elements = document.getElementsByClassName('weavr-component_no-token')
    for (let element of elements) {
        element.style = 'display: grid;'
    }

    initialiseAsPerPath(false)

}

function initialiseComponentsPostToken() {
    const elements = document.getElementsByClassName('weavr-component_token')
    for (let element of elements) {
        element.style = 'display: grid;'
    }

    const msgElemetns = document.getElementsByClassName('weavr-component_no-token-msg')
    for (let element of msgElemetns){
        element.style = 'display: none;'
    }
    // sample.js
    initialiseAsPerPath(true)
}


function initialiseAsPerPath(hasToken) {
    if (window.location.pathname.endsWith('/playground.html')) {
        hideInitMessage()
        initStateComponents(hasToken)
    } else {
        initComponents(hasToken)
    }
}


//
// Get HTML Elements and set necessary listeners
//

/** @type {HTMLInputElement} */
const uiKeyElement = getElement('input-ui-key')
/** @type {HTMLInputElement} */
const apiKeyElement = getElement('input-api-key')
/** @type {HTMLInputElement} */
const usernameElement = getElement('input-username')
/** @type {HTMLInputElement} */
const passwordElement = getElement('input-password')
/** @type {HTMLInputElement} */
const challengeElement = getElement('input-challenge')

/** @type {HTMLTextAreaElement} */
const authTokenElement = getElement('input-auth-key')

const initMessageElement = getElement('init-message')

const initForm = getElement('init-form')
const initFormListener = () => (initForm.addEventListener('submit', submitLoginForm))
isElementAvailable(initForm, 'ui-form', initFormListener)

const uiForm = getElement('ui-form')
const uiFormListener = () => (uiForm.addEventListener('submit', submitUiForm))
isElementAvailable(uiForm, 'ui-form', uiFormListener)

const challengeForm = getElement('challenge-form')
const challengeFormListener = () => (challengeForm.addEventListener('submit', verifyChallenge))
isElementAvailable(challengeForm, 'challenge-form', challengeFormListener)

/** @type {HTMLButtonElement} */
const btnChallenge = getElement('btn-challenge')
const btnChallengeClick = isElementAvailable(btnChallenge, 'btn-challenge', function () {
    btnChallenge.onclick = issueChallenge
})

/** @type {HTMLButtonElement} */
const initStateBtn = getElement('init-btn') || null


function isElementAvailable(element, id, callback) {
    if (typeof callback === 'function') {
        element ? callback(event) : console.warn(`#${id} Element not found`)
    } else {
        console.warn('No Callback Found')
        return
    }
}


loadKeys()
